import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AuthService } from '@auth0/auth0-angular';
import { RouterModule } from '@angular/router';
import { AuthenticationServiceComponent } from 'src/app/authentication-service/authentication-service.component';

@Component({
  selector: 'app-nav-bar',
  imports: [CommonModule, RouterModule],
  standalone: true,
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  isExpanded = false;

  constructor(public auth: AuthService, public authenticationService: AuthenticationServiceComponent) {}

  toggleSidebar() {
    this.isExpanded = !this.isExpanded;
  }

  checkLogin() {
    if(localStorage.getItem("authToken")){
      localStorage.setItem("authToken",JSON.stringify(this.auth))
    }

  }

  logout() {
    localStorage.removeItem("authToken")
  }

  ngOnInit(){
  this.authenticationService.login
    console.log(this.auth)
  }
}
